import React, {useState, useEffect, useCallback} from "react"
import Card from "react-bootstrap/Card";
import API from "../../utils/API/apiRoutes";


function Mortgage() {

    const [rate, setRate] = useState();
    const [downPayment, setDownPayment] = useState();
    const [monthlyInterest, setMonthlyInterest] = useState();
    const [monthlyPayment, setMonthlyPayment] = useState();
    const [fixedRate, setFixedRate] = useState();

    const [homeValue, setHomeValue] = useState();


    const getRate = useCallback(() => {
        API.getRate()
        .then(res => {
            console.log(res.data)
            setRate(res.data)
            setMonthlyInterest(res.data / 12)
        }, 2000)
    }, [])

    useEffect(() => {
        getRate()
    }, [getRate])


    function calculateDownPayment(number) {
        // factor down payment of 20%
        let downPayment = number * .20
        setDownPayment(downPayment.toLocaleString())

        // factor monthly payment

        let monthlyPayment = (number - downPayment +  monthlyInterest) * (fixedRate * 12)

        setDownPayment(monthlyPayment.toLocaleString())
    }

    console.log(fixedRate)

    return (
        <>
         <Card style={{ width: "18rem"}}>

            <Card.Body>
            <Card.Title>
                How much is the home you are looking to purchase?
                <input 
                onChange={(e) => calculateDownPayment(e.target.value)}></input>



            </Card.Title>
            <Card.Title>
                20% Down Payment for this home: {downPayment}

            </Card.Title>
            <Card.Title>
                Fixed Rate Length:
                <br></br>
                <button onClick={(e) => setFixedRate(e.target.value)}>15</button>
                <br></br>
                <button onClick={(e) => setFixedRate(e.target.value)}>30</button>

            </Card.Title>
            <Card.Title>
                Monthly Payment: {monthlyPayment}

            </Card.Title>
            <p>Current Mortgage Rate: {rate}</p>
            </Card.Body>
         </Card>
        </>
    )
}

export default Mortgage
